-- Popolamento completo dei contenuti per la pagina Chi Siamo
-- Questo script aggiunge tutti i contenuti mancanti per la cronologia dettagliata dei maestri

INSERT INTO page_contents (page_name, section_key, section_title, content_type, content, editor_instructions, display_order) VALUES

-- Taehye sunim - Cronologia dettagliata
('chi-siamo', 'taehye-cronologia-1978', 'Taehye 1978', 'text', 'Laureatosi in lettere ha svolto in seguito lavori e ricerca all''università di Helsinki. Ha tradotto testi buddhisti e scritto articoli di vario genere su riviste specialistiche. Sono più di dieci i suoi libri pubblicati in Finlandia.', 'Cronologia Taehye sunim - 1978', 11),

('chi-siamo', 'taehye-cronologia-1982', 'Taehye 1982', 'text', 'Viene ordinato monaco novizio in Thailandia dal Maestro Phra Krusangvorn Samadhivat presso il monastero Wat Pleng Vipassana e prende il nome monastico Mahapañña (Maha=grande; pañña=saggezza).', 'Cronologia Taehye sunim - 1982', 12),

('chi-siamo', 'taehye-cronologia-1987', 'Taehye 1987', 'text', 'Riceve in Corea del sud l''ordinazione completa di bhikshu dal Maestro Il Gak 一覺, secondo la tradizione Seon 禪, e prende il nome monastico Taehye (Tae 大 = Grande; Hye 慧 = Saggezza).', 'Cronologia Taehye sunim - 1987', 13),

('chi-siamo', 'taehye-cronologia-1992', 'Taehye 1992', 'text', 'Giunto in Italia ha iniziato a svolgere la sua attività presso il tempio "Pagoda", un piccolo eremo buddhista sito in località Pieve a Socana di Castel Focognano (AR).', 'Cronologia Taehye sunim - 1992', 14),

('chi-siamo', 'taehye-cronologia-1999', 'Taehye 1999', 'text', 'In Birmania riceve la riordinazione di bhikkhu secondo la scuola Theravada dal Maestro U Paññadipa, abate del International Meditation Center di Yangon.', 'Cronologia Taehye sunim - 1999', 15),

('chi-siamo', 'taehye-cronologia-2000', 'Taehye 2000', 'text', 'Lascia la "Pagoda" e fonda il Tempio Musang Am (Eremo della Non Forma) in località Monti San Lorenzo di Lerici(SP), dove tuttora risiede ed impartisce l''insegnamento buddhista.', 'Cronologia Taehye sunim - 2000', 16),

('chi-siamo', 'taehye-oggi', 'Taehye Oggi', 'text', 'Taehye sunim è presidente, dal 1997, dell''Associazione Culturale "Comunità Bodhidharma" ed inoltre guida spirituale dell''omonima associazione "Bodhidharma" in Finlandia. È impegnato in diverse attività di Dharma e insegnamento delle pratiche di meditazione al Tempio Musang Am 無相庵 di Lerici (SP) ed a Genova. Dal 05/15 è ufficialmente Ministro di Culto dell''Unione Buddhista Italiana.', 'Taehye sunim - Situazione attuale', 17),

-- Taeri sunim - Cronologia dettagliata
('chi-siamo', 'taeri-cronologia-1993-1994', 'Taeri 1993-1994', 'text', 'Postulante alla Pagoda di Arezzo sotto la guida del Maestro Taehye Sunim (tradizione coreana).', 'Cronologia Taeri sunim - 1993-1994', 21),

('chi-siamo', 'taeri-cronologia-1994', 'Taeri 1994', 'text', 'Maggio-settembre: ritiro estivo di meditazione a Taiwan sotto la guida del Maestro Ziyuan 自圓. Novembre: al Songgwangsa 松廣 in Corea del Sud. diventa discepolo del Maestro Ilgak 一覺 secondo la tradizione Seon 禪.', 'Cronologia Taeri sunim - 1994', 22),

('chi-siamo', 'taeri-cronologia-1995', 'Taeri 1995', 'text', 'Febbraio: ordinazione (individuale) di bhikshu (monaco) al Songwangsa davanti all''assemblea di 10 Maestri e prende il nome monastico Taeri (Tae 太=Grande; Ri 利=Beneficio).', 'Cronologia Taeri sunim - 1995', 23),

('chi-siamo', 'taeri-cronologia-1996', 'Taeri 1996', 'text', 'Aprile: ordinazione di novizio nell''Ordine Chogye 曹溪宗 al Chikjisa 直指寺 in Corea del Sud. Ottobre: Ordinazione monastica e di bodhisattva nell''Ordine Chogye al Tongdosa通度寺 in Corea del Sud.', 'Cronologia Taeri sunim - 1996', 24),

('chi-siamo', 'taeri-cronologia-1994-1997', 'Taeri 1994-1997', 'text', 'Ritiri di meditazione invernali ed estivi di tre mesi al Songgwangsa 松廣寺 in Corea del Sud. Durante i mesi liberi studio della lingua coreana.', 'Cronologia Taeri sunim - 1994-1997', 25),

('chi-siamo', 'taeri-cronologia-1997-1999', 'Taeri 1997-1999', 'text', 'Soggiorno in Thailandia e Myanmar nei monasteri della tradizione Theravada, ordinazione monastica (upasampadā) e prende il nome monastico Kumara (giovane). Seguono studi della dottrina, meditazione sotto la guida del Maestro Paññadipa. Visite prolungate in Sri Lanka nei luoghi sacri e studio.', 'Cronologia Taeri sunim - 1997-1999', 26),

('chi-siamo', 'taeri-cronologia-1999-2001', 'Taeri 1999-2001', 'text', 'Soggiorno a Taiwan nei monasteri della tradizione cinese Mahayana, ordinazione monastica e di bodhisattva, ritiri di meditazione, recitazioni di sutra e cerimonie, apprendimento della lingua cinese di base.', 'Cronologia Taeri sunim - 1999-2001', 27),

('chi-siamo', 'taeri-cronologia-2004', 'Taeri 2004', 'text', '"Diploma in Buddhism" conseguito al Pali & Buddhist University of Colombo Sri Lanka.', 'Cronologia Taeri sunim - 2004', 28),

('chi-siamo', 'taeri-cronologia-2011', 'Taeri 2011', 'text', 'Laurea in Scienze dell''Educazione e della Formazione, Università degli Studi di Padova.', 'Cronologia Taeri sunim - 2011', 29),

('chi-siamo', 'taeri-cronologia-2013', 'Taeri 2013', 'text', 'Laurea magistrale in Scienze Umane e Pedagogiche, Università degli studi di Padova.', 'Cronologia Taeri sunim - 2013', 30),

('chi-siamo', 'taeri-oggi', 'Taeri Oggi', 'text', 'Taeri sunim è attualmente impegnato in diverse attività di Dharma e insegnamento delle pratiche di meditazione a Padova, al monastero Musang Am無相庵 di Lerici (SP) e in collaborazione con vari centri della tradizione Theravada dello Sri Lanka presenti in Italia e della Pagoda vietnamita, Chùa Viên Ý, di Polverara (PD). Dal 2018 è ufficialmente Ministro di Culto dell''Unione Buddhista Italiana.', 'Taeri sunim - Situazione attuale', 31),

-- Ven. Kusalananda - Biografia completa
('chi-siamo', 'kusalananda-biografia-1', 'Kusalananda Bio 1', 'text', 'Dopo un percorso spirituale di preparazione passato al tempio Musang am di Lerici sotto la guida del Maestro Tae Hye sunim in qualità di upasaka, decide di "lasciare casa" prendendo i precetti da anagarika.', 'Kusalananda - Biografia parte 1', 41),

('chi-siamo', 'kusalananda-biografia-2', 'Kusalananda Bio 2', 'text', 'A seguire un ritiro di sei mesi in Birmania presso il centro Shwee Oo Min, con il Maestro di meditazione vipassana Ashin-Tejaniya dove viene ordinato Bikkhu, nella tradizione Theravada birmana.', 'Kusalananda - Biografia parte 2', 42),

('chi-siamo', 'kusalananda-biografia-3', 'Kusalananda Bio 3', 'text', 'Risiede attualmente al Tempio Musang am di Lerici presso Comunità Bodhidharma, ed ha contatti con realtà associative legate al mondo del volontariato, come l''Associazione Karuna onlus di Genova, di cui è segretario.', 'Kusalananda - Biografia parte 3', 43),

('chi-siamo', 'kusalananda-biografia-4', 'Kusalananda Bio 4', 'text', 'Ha svolto per anni attività di volontario nell''ambito del progetto Liberation prison project di Milano, portando la meditazione ai detenuti del penitenziario di Massa-Carrara e talvolta altri penitenziari, es La-Spezia-Migliarina e Bollate.', 'Kusalananda - Biografia parte 4', 44),

('chi-siamo', 'kusalananda-biografia-5', 'Kusalananda Bio 5', 'text', 'È dal 2009 in contatto collaborativo con il Tempio srilankese Sambudu di Genova di cui è promotore - segretario.', 'Kusalananda - Biografia parte 5', 45),

('chi-siamo', 'kusalananda-biografia-6', 'Kusalananda Bio 6', 'text', 'Attivo come traduttore, organizzatore di eventi di Dharma e insegnante di meditazione, collega spesso gruppi eterogenei di persone quali escursionisti, praticanti di yoga, musicisti, per diffondere il Dharma in "esterna" negli ambiti più disparati.', 'Kusalananda - Biografia parte 6', 46),

('chi-siamo', 'kusalananda-biografia-7', 'Kusalananda Bio 7', 'text', 'Musicista, pianista e compositore, sviluppa attività di raccordo tra arte, creatività e meditazione spesso in collaborazione con altri musicisti e poeti.', 'Kusalananda - Biografia parte 7', 47),

('chi-siamo', 'kusalananda-biografia-8', 'Kusalananda Bio 8', 'text', 'Dal 2018 è ufficialmente Ministro di Culto dell''Unione Buddhista Italiana.', 'Kusalananda - Biografia parte 8', 48),

('chi-siamo', 'kusalananda-biografia-9', 'Kusalananda Bio 9', 'text', 'Dal 2019 attivo nel progetto "Respirando" presso ISTITUTO COMPRENSIVO ARCOLA/AMEGLIA nell''ambito della scuola primaria, proponendo tra l''altro, giochi concentrativi rivolti al singolo, giochi di gruppo, esercizi di respirazione consapevole, circle time, interazione amichevole, dialogo, pratica della "gentilezza".', 'Kusalananda - Biografia parte 9', 49)

ON CONFLICT (page_name, section_key) DO NOTHING;

